import ImageMacBook from "./og-macbook-pro-202301.jpg";
import { useCart } from "../../CartContext/CartContext";
import "./StyleProductMacBook.css";
import { Images } from "../../Images/Images";

const loop = () => {
  return(
    <div>
      {Images.map((image)=> {
        const newImage = image;
        return newImage; 
      })}
    </div>
  );
}

export const ProductMacBook = [
  {
    id: "1mac",
    name: "MacBookPro",
    price: 1,
    details: "cok guzel pc",
    image: loop(),
  },
  {
    id: "2mac",
    name: "HP Pavilion",
    price: 2,
    details: "cok guzel pc",
    image: ImageMacBook,
  },
  {
    id: "3mac",
    name: "Siuuuuuuuu",
    price: 3,
    details: "cok guzel pc",
    image: ImageMacBook,
  },
  {
    id: "4mac",
    name: "Siuuuuuuuu",
    price: 3,
    details: "cok guzel pc",
    image: ImageMacBook,
  },
  {
    id: "5mac",
    name: "Siuuuuuuuu",
    price: 3,
    details: "cok guzel pc",
    image: ImageMacBook,
  },
  {
    id: "6mac",
    name: "Siuuuuuuuu",
    price: 3,
    details: "cok guzel pc",
    image: ImageMacBook,
  },
];

const CardMacBook = () => {
  const { addToCart } = useCart();

  const handleAddToCart = (product) => {
    addToCart(product);
  }

  return (
    <div className="card-container d-flex flex-wrap justify-content-center gap-5">
      {ProductMacBook.map((product) => (
        <div className="card" style={{ width: "25rem"}} key={product.id}>
          <img
            src={product.image}
            className="card-img-top"
            alt={product.name}
          />
          <div className="card-body">
            <h2 className="card-title d-flex justify-content-center">{product.name}</h2>
            <p className="card-text ">Price: {product.price} TL</p>
            <p className="card-text">Details: {product.details}</p>
            <button 
            type="button" 
            className="btn btn-danger"
            onClick={() => handleAddToCart(product)}
            >
              Add to chart</button>
          </div>

        </div>
      ))}
    </div>
  );
};

export default CardMacBook;
