import ImageIPad from "./Apple-iPad-10th-gen-hero-221018.jpg.og.jpg";
import { useCart } from "../../CartContext/CartContext";
import { Images } from "../../Images/Images";

export const ProductIPad = [
  {
    id: "1ipad",
    name: "IPad",
    price: 1,
    details: "cok guzel pc",
    image: ImageIPad,
  },
  {
    id: "2ipad",
    name: "IPad 2",
    price: 2,
    details: "cok guzel pc",
    image: ImageIPad,
  },
  {
    id: "3ipad",
    name: "IPad 3",
    price: 3,
    details: "cok guzel pc",
    image: ImageIPad,
  },
  {
    id: "4ipad",
    name: "IPad 4",
    price: 3,
    details: "cok guzel pc",
    image: ImageIPad,
  },
  {
    id: "5ipad",
    name: "IPad 5",
    price: 3,
    details: "cok guzel pc",
    image: ImageIPad,
  },
  {
    id: "6ipad",
    name: "IPad 6",
    price: 3,
    details: "cok guzel pc",
    image: ImageIPad,
  },
];

const CardIPad = () => {

  const { addToCart } = useCart();

  const handleAddToCart = (product) => {
    addToCart(product);
  };

  return (
    <div className="card-container d-flex flex-wrap justify-content-center gap-5">
      {ProductIPad.map((product) => (
        <div className="card" style={{ width: "25rem"}} key={product.id}>
          <img
            src={product.image}
            className="card-img-top"
            alt={product.name}
          />
          <div className="card-body">
            <h2 className="card-title d-flex justify-content-center">{product.name}</h2>
            <p className="card-text ">Price: {product.price} TL</p>
            <p className="card-text">Details: {product.details}</p>
            <button 
            type="button" 
            className="btn btn-danger"
            onClick={()=> handleAddToCart(product)}
            >Add to chart</button>
          </div>

        </div>
      ))}
    </div>
  );
};

export default CardIPad;
