import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

const alertMessage = () => {
  toast.success("Ürün sepete eklendi", {
    position: "top-right",
    autoClose: 3000,
    hideProgressBar: false,
    closeOnClick: true,
    pauseOnHover: true,
    draggable: true,
    progress: undefined,
  });
};

export default alertMessage;
